r = 512/2
x_range = math.floor(512/math.sqrt(2))
y_range = math.floor(512/math.sqrt(2))
x = 0
y = 0
n=0

projection_value = np.arange(5*x_range*y_range, dtype = 'f').reshape(5,x_range,y_range)
result_value = np.arange(x_range*y_range, dtype = 'f').reshape(x_range,y_range)
for x in range(x_range):
    for y in range(y_range):
        result_value[x][y] = 1000

##각각의 픽셀별 데이터 구하기

for n in range (5):
    theta = 2*math.pi*angle[n]/360
    for x in range(x_range):
        for y in range(y_range):
            tan = math.tan(theta)
            if theta == 0:
                s = r - (r/math.sqrt(2) - y)
            else:
                x_theta = r/math.sqrt(2)+r*math.cos(theta)
                y_theta = r/math.sqrt(2)+r*math.sin(theta)
                xs = (tan**2*x + x_theta)/(tan**2+1) - tan*(y-y_theta)/(tan**2+1)
                ys = -1/math.tan(theta)*(xs-x_theta)+y_theta
                s = r - math.sqrt((x_theta-xs)**2 + (y_theta-ys)**2) ## s 거리 구하기 ##

            ## s에 해당하는 값 예측
            projection_value[n][x][y] = g[n][math.floor(s)] + (g[n][math.ceil(s)] - g[n][math.floor(s)]) * (s-math.floor(s))
            ## 최소값으로 결과값 결정하기
            if projection_value[n][x][y] <= result_value[x][y]:
                result_value[x][y] = projection_value[n][x][y]

print(result_value)
np.savetxt("result.csv", result_value, delimiter=",")
